import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Outlet } from 'react-router-dom';
import './App.css';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import QuizMasterDashboard from './components/Dashboard/QuizMasterDashboard';
import QuizTakerDashboard from './components/Dashboard/QuizTakerDashboard';
import QuizList from './components/QuizList';
import Quiz from './components/Quiz';
import NavBar from './components/NavBar';
import AddQuiz from './components/addQuiz';

const PrivateRoute = ({ role, requiredRole, redirectPath = '/login' }) => {
  if (!role || (requiredRole && role !== requiredRole)) {
    return <Navigate to={redirectPath} replace />;
  }
  return <Outlet />;
};

const App = () => {
  const role = localStorage.getItem('role');

  return (
    <Router>
      <div className="App">
        <NavBar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          <Route element={<PrivateRoute role={role} />}>
            <Route path="/dashboard" element={role === 'quizmaster' ? <QuizMasterDashboard /> : <QuizTakerDashboard />} />
            <Route path="/quiztaker/dashboard" element={<QuizTakerDashboard />} />
            <Route path="/quizmaster/dashboard" element={<QuizMasterDashboard />} />
            <Route path="/quizzes/:genre" element={<QuizList />} />
            <Route path="/quiz/:id" element={<Quiz />} />
            <Route path="/add-quiz" element={<PrivateRoute role={role} requiredRole="quizmaster"><AddQuiz /></PrivateRoute>} />
          </Route>
        </Routes>
      </div>
    </Router>
  );
};

export default App;
