import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import './App.css';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import QuizMasterDashboard from './components/Dashboard/QuizMasterDashboard';
import QuizTakerDashboard from './components/Dashboard/QuizTakerDashboard';
import QuizList from './components/QuizList';
import Quiz from './components/Quiz';
import NavBar from './components/NavBar';

const App = () => {
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  return (
    <Router>
      <div className="App">
        <NavBar />
        <Switch>
          <Route exact path="/" component={Home} />
          <Route exact path="/login" component={Login} />
          <Route exact path="/register" component={Register} />
          <PrivateRoute
            exact
            path="/dashboard"
            component={role === 'quizmaster' ? QuizMasterDashboard : QuizTakerDashboard}
            role={role}
          />
          <PrivateRoute exact path="/quiztaker/dashboard" component={QuizTakerDashboard} role="quiztaker" />
          <PrivateRoute exact path="/quizmaster/dashboard" component={QuizMasterDashboard} role="quizmaster" />
          <PrivateRoute exact path="/quizzes/:genre" component={QuizList} />
          <PrivateRoute exact path="/quiz/:id" component={Quiz} />
          
        </Switch>
      </div>
    </Router>
  );
};

const PrivateRoute = ({ component: Component, role, ...rest }) => (
    <Route
      {...rest}
      render={props => {
        const token = localStorage.getItem('token');
        const userRole = localStorage.getItem('role');
        
        if (!token) {
          return <Redirect to="/login" />;
        }
  
        if (role && userRole !== role) {
          return <Redirect to="/login" />;
        }
  
        return <Component {...props} />;
      }}
    />
  );
  

export default App;
