import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../api';
import Swal from 'sweetalert2';

const Login = () => {
  const [formData, setFormData] = useState({ username: '', password: '', role: 'quiztaker' });
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await login(formData);
      localStorage.setItem('token', data.token);
      localStorage.setItem('role', data.role);
      Swal.fire('Success', 'Login successful!', 'success');
      if (data.role === 'quiztaker') {
        navigate('/quizzes'); // Redirect to quizzes list for quiz takers
      } else if (data.role === 'quizmaster') {
        navigate('/add-quiz'); // Redirect to add quiz page for quiz masters
      }
    } catch (error) {
      Swal.fire('Error', 'Invalid username, password, or role', 'error');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Username"
        value={formData.username}
        onChange={(e) => setFormData({ ...formData, username: e.target.value })}
      />
      <input
        type="password"
        placeholder="Password"
        value={formData.password}
        onChange={(e) => setFormData({ ...formData, password: e.target.value })}
      />
      <select
        value={formData.role}
        onChange={(e) => setFormData({ ...formData, role: e.target.value })}
      >
        <option value="quiztaker">Quiz Taker</option>
        <option value="quizmaster">Quiz Master</option>
      </select>
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
