import React, { useState } from 'react';
import { createQuiz } from '../api';
import Swal from 'sweetalert2';

const AddQuiz = () => {
  const [quizData, setQuizData] = useState({
    title: '',
    genre: '',
    questions: [{ questionText: '', options: ['', '', '', ''], correctAnswer: '' }],
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createQuiz(quizData);
      Swal.fire('Success', 'Quiz added successfully!', 'success');
      setQuizData({
        title: '',
        genre: '',
        questions: [{ questionText: '', options: ['', '', '', ''], correctAnswer: '' }],
      });
    } catch (error) {
      Swal.fire('Error', 'Error adding quiz', 'error');
    }
  };

  const handleQuestionChange = (index, field, value) => {
    const updatedQuestions = [...quizData.questions];
    updatedQuestions[index][field] = value;
    setQuizData({ ...quizData, questions: updatedQuestions });
  };

  const handleOptionChange = (index, optIndex, value) => {
    const updatedQuestions = [...quizData.questions];
    updatedQuestions[index].options[optIndex] = value;
    setQuizData({ ...quizData, questions: updatedQuestions });
  };

  const addQuestion = () => {
    setQuizData({
      ...quizData,
      questions: [...quizData.questions, { questionText: '', options: ['', '', '', ''], correctAnswer: '' }],
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Title"
        value={quizData.title}
        onChange={(e) => setQuizData({ ...quizData, title: e.target.value })}
      />
      <input
        type="text"
        placeholder="Genre"
        value={quizData.genre}
        onChange={(e) => setQuizData({ ...quizData, genre: e.target.value })}
      />
      {quizData.questions.map((question, index) => (
        <div key={index}>
          <input
            type="text"
            placeholder="Question"
            value={question.questionText}
            onChange={(e) => handleQuestionChange(index, 'questionText', e.target.value)}
          />
          {question.options.map((option, optIndex) => (
            <input
              key={optIndex}
              type="text"
              placeholder={`Option ${optIndex + 1}`}
              value={option}
              onChange={(e) => handleOptionChange(index, optIndex, e.target.value)}
            />
          ))}
          <input
            type="text"
            placeholder="Correct Answer"
            value={question.correctAnswer}
            onChange={(e) => handleQuestionChange(index, 'correctAnswer', e.target.value)}
          />
        </div>
      ))}
      <button type="button" onClick={addQuestion}>
        Add Question
      </button>
      <button type="submit">Add Quiz</button>
    </form>
  );
};

export default AddQuiz;
