import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const navStyle = {
    display: 'flex',
    justifyContent: 'flex-end',
    backgroundColor: '#333',
    padding: '10px',
  };

  const navLinkStyle = {
    color: 'white',
    margin: '0 10px',
    textDecoration: 'none',
    fontSize: '16px',
  };

  const buttonStyle = {
    ...navLinkStyle,
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    padding: '0',
  };

  const genresContainerStyle = {
    textAlign: 'center',
    marginTop: '50px',
  };

  return (
    <div>
      <nav style={navStyle}>
        <div style={{ display: 'flex', gap: '20px' }}>
          {isLoggedIn ? (
            <>
              <Link to="/create-quiz" style={navLinkStyle}>Create Quiz</Link>
              <button onClick={handleLogout} style={buttonStyle}>Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" style={navLinkStyle}>Login</Link>
              <Link to="/register" style={navLinkStyle}>Register</Link>
            </>
          )}
        </div>
      </nav>
      <div style={genresContainerStyle}>
        <h1>QuizMaker - By AspireNex</h1>
        <div>
          <h2>Genres:</h2>
          <ul>
            <li><Link to="/quizzes/Science">Science</Link></li>
            <li><Link to="/quizzes/Math">Math</Link></li>
            <li><Link to="/quizzes/History">History</Link></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Home;
