import React, { useEffect, useState } from 'react';
import { useParams} from 'react-router-dom';
import { getQuizById } from '../api';
import Swal from 'sweetalert2';

const Quiz = () => {
  const { id } = useParams();
  const [quiz, setQuiz] = useState(null);
  const [score, setScore] = useState(null);

  useEffect(() => {
    const fetchQuiz = async () => {
      try {
        const { data } = await getQuizById(id);
        setQuiz(data);
      } catch (error) {
        console.error('Error fetching quiz:', error);
      }
    };
    fetchQuiz();
  }, [id]);

  const handleQuizSubmit = () => {
    const totalQuestions = quiz.questions.length; // Replace with actual logic
    const correctAnswers = quiz.questions.filter(question => {
      // Replace with your actual correct answer checking logic
      return question.selectedOption === question.correctAnswer;
    }).length;
    const percentageScore = (correctAnswers / totalQuestions) * 100;

    // Display score using SweetAlert with Retake button
    Swal.fire({
      title: 'Quiz Completed!',
      html: `You scored ${percentageScore}%`,
      icon: 'success',
      showCancelButton: true,
      confirmButtonText: 'Home',
      cancelButtonText: 'Retake Quiz',
    }).then((result) => {
      if (result.isConfirmed) {
        // Redirect to home page
        window.location.href = '/';
      } else if (result.dismiss === Swal.DismissReason.cancel) {
        // Reset quiz state to retake quiz
        const resetQuestions = quiz.questions.map(question => ({
          ...question,
          selectedOption: null,
        }));
        setQuiz({ ...quiz, questions: resetQuestions });
        setScore(null); // Reset score
      }
    });

    // Optionally, you can set the score in state to display it in the UI
    setScore(percentageScore);
  };

  if (!quiz) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{quiz.title}</h1>
      <p>Author: {quiz.author}</p>
      <div>
        {quiz.questions.map((question, index) => (
          <div key={index}>
            <h3>{question.questionText}</h3>
            <ul>
              {question.options.map((option, optIndex) => (
                <li key={optIndex}>
                  <input
                    type="radio"
                    name={`question${index}`}
                    value={option}
                    onChange={(e) => {
                      const newQuestions = [...quiz.questions];
                      newQuestions[index].selectedOption = e.target.value;
                      setQuiz({ ...quiz, questions: newQuestions });
                    }}
                  />
                  {option}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      <button onClick={handleQuizSubmit}>Submit Quiz</button>
      {score && <p>Your Score: {score}%</p>}
    </div>
  );
};

export default Quiz;
