import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../api';
import Swal from 'sweetalert2';

const Login = () => {
  const [formData, setFormData] = useState({ username: '', password: '', role: 'quiztaker' });
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await login(formData);
      localStorage.setItem('token', data.token);
      localStorage.setItem('role', data.role);
      Swal.fire('Success', 'Login successful!', 'success');
      navigate('/'); // Redirect to homepage after successful login
    } catch (error) {
      Swal.fire('Error', 'Invalid username, password, or role', 'error');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Username</label>
        <input
          type="text"
          placeholder="Username"
          value={formData.username}
          onChange={(e) => setFormData({ ...formData, username: e.target.value })}
          required
        />
      </div>
      <div className="form-group">
        <label>Password</label>
        <input
          type="password"
          placeholder="Password"
          value={formData.password}
          onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          required
        />
      </div>
      <div className="form-group">
        <label>Role</label>
        <select
          value={formData.role}
          onChange={(e) => setFormData({ ...formData, role: e.target.value })}
        >
          <option value="quiztaker">Quiz Taker</option>
          <option value="quizmaster">Quiz Master</option>
        </select>
      </div>
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
