import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getQuizzesByGenre } from '../api';

const QuizMath = () => {
  const [quizzes, setQuizzes] = useState([]);
  const genre = 'Math';

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const { data } = await getQuizzesByGenre(genre);
        setQuizzes(data);
      } catch (error) {
        console.error('Error fetching Math quizzes:', error);
      }
    };
    fetchQuizzes();
  }, []);

  return (
    <div>
      <h2>Math Quizzes</h2>
      <div>
        {quizzes.map((quiz) => (
          <div key={quiz._id}>
            <h3>{quiz.title}</h3>
            <p>Author: {quiz.author}</p>
            <Link to={`/quiz/${quiz._id}`}>
              <button>Start Quiz</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizMath;
