import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import QuizMasterDashboard from './components/Dashboard/QuizMasterDashboard'; // Correct import
import QuizTakerDashboard from './components/Dashboard/QuizTakerDashboard'; // Correct import
import QuizList from './components/QuizList';
import Quiz from './components/Quiz';
import NavBar from './components/NavBar';

const App = () => {
  const role = localStorage.getItem('role');

  return (
    <Router>
      <div className="App">
        <NavBar />
        <Routes>
          <Route exact path="/" element={<Home />} />
          <Route exact path="/login" element={<Login />} />
          <Route exact path="/register" element={<Register />} />
          <PrivateRoute
            exact
            path="/dashboard"
            element={role === 'quizmaster' ? <QuizMasterDashboard /> : <QuizTakerDashboard />}
            role={role}
          />
          <PrivateRoute exact path="/quiztaker/dashboard" element={<QuizTakerDashboard />} role="quiztaker" />
          <PrivateRoute exact path="/quizmaster/dashboard" element={<QuizMasterDashboard />} role="quizmaster" />
          <PrivateRoute exact path="/quizzes/:genre" element={<QuizList />} />
          <PrivateRoute exact path="/quiz/:id" element={<Quiz />} />
        </Routes>
      </div>
    </Router>
  );
};

const PrivateRoute = ({ element: Element, role, ...rest }) => (
  <Route
    {...rest}
    element={
      <Element
        role={role}
        navigate={props => {
          const token = localStorage.getItem('token');
          const userRole = localStorage.getItem('role');

          if (!token) {
            return <Navigate to="/login" />;
          }

          if (role && userRole !== role) {
            return <Navigate to="/login" />;
          }

          return <Element {...props} />;
        }}
      />
    }
  />
);

export default App;
