import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const navStyle = {
    display: 'flex',
    justifyContent: 'flex-end',
    backgroundColor: '#333',
    padding: '10px',
  };

  const navLinkStyle = {
    color: 'white',
    margin: '0 10px',
    textDecoration: 'none',
  };

  const genresContainerStyle = {
    textAlign: 'center',
    marginTop: '50px',
  };

  return (
    <div>
      <nav style={navStyle}>
        {isLoggedIn ? (
          <>
            <Link to="/create-quiz" style={navLinkStyle}>Create Quiz</Link>
            <button onClick={handleLogout} style={{ ...navLinkStyle, background: 'none', border: 'none', cursor: 'pointer' }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" style={navLinkStyle}>Login</Link>
            <Link to="/register" style={navLinkStyle}>Register</Link>
          </>
        )}
      </nav>
      <div style={genresContainerStyle}>
        <h1>QuizMaker - By AspireNex</h1>
        <div>
          <h2>Genres:</h2>
          <ul>
            <li><Link to="/quizzes/Science">Science</Link></li>
            <li><Link to="/quizzes/Math">Math</Link></li>
            <li><Link to="/quizzes/History">History</Link></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Home;
